CREATE TABLE users_aula48(
	id 	VARCHAR (255) PRIMARY KEY,
    name TEXT,
    email TEXT,
    password TEXT
);
CREATE TABLE recipes_aula48 (
	recipe_id VARCHAR (255) PRIMARY KEY,
	user_id VARCHAR (255),
	title TEXT,
	description TEXT,
	createdAt BIGINT,
    FOREIGN KEY (user_id) REFERENCES users_aula48 (id)
);
CREATE TABLE users_relation_aula48(
	user_id VARCHAR (255),
    user_to_follow_id VARCHAR (255),
    PRIMARY KEY (user_id, user_to_follow_id),
    FOREIGN KEY (user_id) REFERENCES users_aula48 (id),
    FOREIGN KEY (user_to_follow_id) REFERENCES users_aula48 (id)
);


